package loop;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		while (a<=10)
		{
			System.out.println(a);
			a++;
		}
		/////////////////////////////
		int b=2;
		while(b<=20)
		{
			System.out.println(b);
			b=b+2;
		}
		////////////////////////////
		int c=250;
		
		while(c>=25)
		{
			System.out.println(c);
			c=c-25;
		}
		
		String name ="sanjay";
		int s=1;
		while( s <=10) {
			System.out.println(name);
			s++;
		}
		
		
		
		
		
		
		
		
		
		
		
	}

}
